mapper.select:
SELECT

from question q LEFT JOIN category c
on q.category_id = c.id

    where q.user_id = #{userId}

ORDER BY q.id DESC
LIMIT #{offset},#{limit}

SELECT

from question q LEFT JOIN category c
on q.category_id = c.id



        q.user_id = #{userId}


        and q.category_id = #{categoryId}



ORDER BY q.id DESC

LIMIT #{offset},#{limit}

SELECT

from question q LEFT JOIN category c
on q.category_id = c.id


        q.user_id = #{userId}


        and q.category_id = #{categoryId}



ORDER BY q.id DESC


    LIMIT #{offset}, #{limit}
